mapper.select:
select
bi.*,
CONCAT(si.shelf_name,'排',ci.s_column,'列',ci.s_row,'层') as location,
ii.item_name,
binf.box_state,
binf.box_cell_id,
ii.max_pack_qty,
ii.thickness,
su.supplier_code,
su.supplier_name,
sii.sub_inventory_code,
sii.sub_inventory_name,
(ii.thickness*bi.quantity) as total_thickness
from box_item bi
join box_info binf on bi.box_code = binf.box_code
left join cell_info ci on binf.box_cell_id = ci.cell_id
left join shelf_info si on ci.shelf_id = si.shelf_id
left join item_info ii on bi.item_code = ii.item_code
left join bill_in_detail bid on bid.bill_in_detail_id = bi.bill_in_detail_id
left join sub_inventory sii on sii.sub_inventory_id = bi.sub_inventory_id
left join supplier su on su.supplier_code = bid.vendor_code

     and bi.box_code = #{boxCode}
     and bi.item_code = #{itemCode}
     and bi.batch = #{batch}
     and ci.s_row = #{sRow}
     and ci.s_column = #{sColumn}
     and si.shelf_name = #{shelfName}
     and ii.item_name = #{itemName}
     and binf.box_state = #{boxState}
     and bi.sub_inventory_id = #{subInventoryId}

        and bid.vendor_code = #{supplierCode}

    and bi.quantity > 0

order by bi.exp,bi.box_code

select bi.*, ci.s_row, ci.s_column, si.shelf_name, ci.cell_id as box_cell_id from box_item bi join box_info binf on bi.box_code = binf.box_code left join cell_info ci on binf.box_cell_id = ci.cell_id left join shelf_info si on ci.shelf_id = si.shelf_id /*join bill_in_detail bid on bid.bill_in_detail_id = bi.bill_in_detail_id*/ where 1=1 and (bi.quantity - ifnull(bi.forecast_stock_quantity,0) - ifnull(bi.lock_quantity,0)) > 0 and binf.box_state not in (3,4)
and bi.item_code = #{itemCode}
/*and ci.state != 2*/ and binf.has_goods = 1 and bi.sub_inventory_id in (2,4) order by bi.work_order_stock_state desc, (case when bi.sub_inventory_id = 4 then 1 when bi.sub_inventory_id = 2 then 2 end) asc, bi.exp asc, bi.quantity asc, bi.in_time asc
/*and ci.state != 2*/
and bi.batch in
#{batch}
and binf.has_goods = 1 and bi.sub_inventory_id in (4) order by bi.work_order_stock_state desc, bi.exp asc, bi.quantity asc, bi.in_time asc
/*and ci.state != 2*/ and binf.has_goods = 1 and bi.sub_inventory_id in (2) order by bi.work_order_stock_state desc, bi.exp asc, bi.quantity asc, bi.in_time asc

select
*
from box_item
where 1=1

    and sub_inventory_id = #{subInventoryId}


    and item_code = #{itemCode}


    and batch = #{batch}


    and exp = #{exp}

mapper.update:
update box_item
box_code = #{boxCode}, item_code = #{itemCode}, batch = #{batch}, quantity = #{quantity}, work_order_stock_state = #{workOrderStockState}, bill_in_detail_id = #{billInDetailId}, sub_inventory_id = #{subInventoryId}, forecast_stock_quantity = #{forecastStockQuantity}, pd = #{pd}, exp = #{exp}, in_time = #{inTime}, lock_quantity = #{lockQuantity}, worker_order_no = #{workerOrderNo},
where id = #{id}

mapper.select:
select bid.line_location_id from box_item bi left join bill_in_detail bid on bid.bill_in_detail_id = bi.bill_in_detail_id where 1=1
and bi.sub_inventory_id = #{subInventoryId}
group by bid.line_location_id

select bi.*,
ci.s_column, ci.s_row, ci.state as cell_state, ii.item_name, ii.unit, binf.box_state, binf.box_cell_id, ii.max_pack_qty,
bid.vendor_code as supplier_code, bid.vendor_name as supplier_name, bid.bill_id,
sii.sub_inventory_code, sii.slotting, sii.sub_inventory_name, ii.inventory_item_id from box_item bi join box_info binf on bi.box_code = binf.box_code join cell_info ci on binf.box_cell_id = ci.cell_id left join item_info ii on bi.item_code = ii.item_code left join bill_in_detail bid on bid.bill_in_detail_id = bi.bill_in_detail_id left join sub_inventory sii on sii.sub_inventory_id = bi.sub_inventory_id where 1=1
and bi.item_code = #{itemCode}
and bi.batch = #{batch}
and bi.exp = #{exp}
and bi.id in
#{id}

select si.shelf_name, ci.s_column, ci.s_row, binf.box_cell_id, bi.item_code, bi.batch, bi.quantity), bi.sub_inventory_id, bi.pd, bi.exp, bi.box_code, ii.item_name, ii.unit from box_item bi left join item_info ii on ii.item_code = bi.item_code join box_info binf on bi.box_code = binf.box_code join cell_info ci on binf.box_cell_id = ci.cell_id left join shelf_info si on ci.shelf_id = si.shelf_id where 1=1
and bi.quantity > #{quantity}
and binf.has_goods = #{hasGood}
and bi.item_code = #{itemCode}
and binf.box_state = #{boxState} and ci.state = #{cellState} and bi.work_order_stock_state = #{workOrderStockState} and bi.sub_inventory_id in (1,4) order by (case when bi.sub_inventory_id = 4 then 1 when bi.sub_inventory_id = 2 then 2 end) asc, bi.exp

SELECT
	bin.box_id,
	bin.box_code,
	bin.box_cell_id,
	bin.box_state,
	bin.has_goods,
    if(has_goods=1,bi.item_code,null) as item_code,
    if(has_goods=1,bi.batch,null) as batch,
    bi.quantity,
    if(has_goods=1,bi.in_time,null) as in_time,
    if(has_goods=1,bi.pd,null) as pd,
    if(has_goods=1,bi.exp,null) as exp,
    if(has_goods=1,bi.sub_inventory_id,1) as sub_inventory_id
FROM
	box_item bi
	JOIN box_info bin ON bi.box_code = bin.box_code
WHERE
    bin.box_code = #{boxCode}
